mapper.select:
select  village,study_level as studyLevel,count(1) as statisticCount
 from sub_poor where 1 = 1

    and account = #{vo.account}


   and semester = #{vo.semester}

GROUP BY village, study_level order by village

SELECT poor.village, poor.study_level AS studyLevel, count(1) as statisticCount, sum(sub.subsidize_money) as totalMoney FROM sub_poor as poor LEFT JOIN sub_subsidize as sub on poor.id = sub.poor_id WHERE 1 = 1
and account = #{vo.account}
and semester = #{vo.semester}
GROUP BY poor.village, poor.study_level ORDER BY poor.village
