mapper.select:
select

from sub_poor
where account = #{poor.account}

    and name like  concat(concat('%',#{poor.username}),'%')


  and id_card=#{poor.idcard}

select count(1)
from sub_poor
where account = #{poor.account}

  and name like concat(concat('%',#{poor.username}),'%')


  and id_card=#{poor.idcard}
